import "./styles.css";

import React, { Component } from "react";
import { render } from "react-dom";

import Carousel from "./carrusel";

class App extends Component {
  state = {
    focus: 0
  };

  onClickPrev = () =>
    this.setState(state => ({
      focus: state.focus - 1 < 0 ? 0 : state.focus - 1
    }));

  onClickNext = () =>
    this.setState(state => ({
      focus: state.focus + 1 >= 6 ? 5 : state.focus + 1
    }));

  render() {
    return (
      <>
        <Carousel component="ul" leftPadding={1} focus={this.state.focus}>
          <li className={this.state.focus === 0 && "has-focus"}>Featured</li>
          <li className={this.state.focus === 1 && "has-focus"}>Highlighted</li>
          <li className={this.state.focus === 2 && "has-focus"}>Top</li>
          <li className={this.state.focus === 3 && "has-focus"}>ForYou</li>
          <li className={this.state.focus === 4 && "has-focus"}>Trending</li>
          <li className={this.state.focus === 5 && "has-focus"}>ComingSoon</li>
        </Carousel>
        <button onClick={this.onClickPrev}>Prev</button>
        <button onClick={this.onClickNext}>Next</button>
      </>
    );
  }
}

render(<App />, document.getElementById("root"));
